import React, { useState } from "react";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, Legend,
} from "recharts";

function GetForecasting() {
  const [inputValue, setInputValue] = useState("");
  const [message, setMessage] = useState("");
  const [forecastedData, setForecastedData] = useState(null);
  const [allocationData, setAllocationData] = useState(null);
  const [activeTab, setActiveTab] = useState("forecast");
  const [loading, setLoading] = useState(false);  // To handle loading state

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = async () => {
    const partsArray = inputValue.split(",").map((item) => item.trim());

    setLoading(true); // Set loading to true when starting the request

    try {
      const response = await axios.post("http://localhost:5000/api/forecast-data", {
        data: partsArray,
      });

      const forecasted = response.data.forecasted_data;
      const allocation = response.data.allocation_data;

      // Validate if forecastedData and allocationData are received properly
      if (forecasted && allocation) {
        setForecastedData(forecasted);
        setAllocationData(allocation);
        setMessage("‚úÖ Forecast data retrieved successfully!");
      } else {
        setMessage("‚ùå Invalid data format received from backend.");
      }

      setInputValue("");
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Failed to retrieve forecast data.");
    } finally {
      setLoading(false); // Set loading to false when request finishes
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-3">üìà Forecast Kitchen Ingredient Usage</h2>

      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        placeholder="Enter number of days (e.g., 3)"
        className="form-control mb-3"
      />
      <button className="btn btn-primary" onClick={handleSubmit} disabled={loading}>
        Submit
      </button>

      {message && <p className="mt-3">{message}</p>}

      {loading && <p>Loading data...</p>} {/* Loading message while fetching data */}

      {forecastedData && allocationData && !loading ? (
        <>
          <ul className="nav nav-tabs mt-4">
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "forecast" ? "active" : ""}`}
                onClick={() => setActiveTab("forecast")}
              >
                Forecasted Ingredients
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "allocation" ? "active" : ""}`}
                onClick={() => setActiveTab("allocation")}
              >
                Stock Allocation
              </button>
            </li>
          </ul>

          <div className="tab-content mt-3">
            {activeTab === "forecast" && (
              <>
                <h4>Total Forecasted Ingredient Quantities</h4>
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={forecastedData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="Ingredient" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="Total_Quantity_Required" fill="#8884d8" />
                  </BarChart>
                </ResponsiveContainer>
              </>
            )}

            {activeTab === "allocation" && (
              <>
                <h4>FIFO Stock Allocation Based on Expiry</h4>
                <div style={{ maxHeight: "300px", overflowY: "auto" }}>
                  <table className="table table-bordered">
                    <thead className="thead-dark">
                      <tr>
                        <th>Ingredient</th>
                        <th>Allocated Quantity</th>
                        <th>Expiry Date</th>
                        <th>Shelf Life (Days)</th>
                      </tr>
                    </thead>
                    <tbody>
                      {allocationData.map((alloc, index) => (
                        <tr key={index}>
                          <td>{alloc.Ingredient}</td>
                          <td>{alloc.Allocated_Quantity}</td>
                          <td>{alloc.Expiry_Date}</td>
                          <td>{alloc.Shelf_Life_Days}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </>
            )}
          </div>
        </>
      ) : (
        !loading && <p>No data available. Please submit the request.</p>  // Message when data is not available yet
      )}
    </div>
  );
}

export default GetForecasting;
