import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'animate.css';

function VegetableDetection() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [resultUrl, setResultUrl] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreview(URL.createObjectURL(file));
    setResultUrl(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const res = await fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    if (data.result_url) {
      setResultUrl(data.result_url);
    }
  };

  return (
    <div className="container py-5">
      <div className="text-center mb-4">
        <h2 className="fw-bold text-success animate__animated animate__fadeInDown">ü•ï Vegetable Detection</h2>
        <p className="text-muted">Upload an image to detect vegetables using AI.</p>
      </div>

      <div className="mb-4 text-center">
        <input type="file" className="form-control w-50 mx-auto" onChange={handleImageChange} />
      </div>

      {preview && (
        <div className="text-center">
          <img
            src={preview}
            alt="Preview"
            className="img-thumbnail animate__animated animate__zoomIn"
            style={{ maxHeight: "300px" }}
          />
          <br /><br />
          <button className="btn btn-success px-4 py-2 animate__animated animate__fadeInUp" onClick={handleUpload}>
            üîç Detect Vegetables
          </button>
        </div>
      )}

      {resultUrl && (
        <div className="text-center mt-5">
          <h4 className="text-primary animate__animated animate__fadeIn">üçÖ Detection Result:</h4>
          <img
            src={resultUrl}
            alt="Result"
            className="img-fluid rounded animate__animated animate__fadeInUp"
            style={{ maxHeight: "300px" }}
          />
        </div>
      )}
    </div>
  );
}

export default VegetableDetection;
