import React, { useEffect, useState } from "react";
import axios from "axios";

const ManageCollections = () => {
  const [ingredients, setIngredients] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    quantity: "",
    unit: "",
    shelf_life: "",
  });

  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const res = await axios.get("http://localhost:5000/ingredients");
      setIngredients(res.data);
    } catch (error) {
      console.error("Error fetching ingredients:", error);
    }
  };

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/ingredients", formData);
      fetchIngredients(); // refresh table
      setFormData({
        name: "",
        quantity: "",
        unit: "",
        shelf_life: "",
      });
    } catch (error) {
      console.error("Error adding ingredient:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üçÖ Manage Ingredients Collection</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: "30px" }}>
        <input
          type="text"
          name="name"
          placeholder="Ingredient Name"
          value={formData.name}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <input
          type="text"
          name="unit"
          placeholder="Unit (e.g., kg)"
          value={formData.unit}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <input
          type="text"
          name="shelf_life"
          placeholder="Shelf Life (e.g., 7 days)"
          value={formData.shelf_life}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <button type="submit" style={buttonStyle}>Add Ingredient</button>
      </form>

      <h3>üìã Current Ingredients:</h3>
      <table style={tableStyle}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Quantity</th>
            <th>Unit</th>
            <th>Shelf Life</th>
          </tr>
        </thead>
        <tbody>
          {ingredients.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.unit}</td>
              <td>{item.shelf_life}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const inputStyle = {
  marginRight: "10px",
  padding: "8px",
  marginBottom: "10px",
};

const buttonStyle = {
  padding: "8px 16px",
  cursor: "pointer",
};

const tableStyle = {
  borderCollapse: "collapse",
  width: "100%",
};

export default ManageCollections;
