import React, { useState } from "react";
import axios from "axios";

function GetForecasting() {
  const [inputValue, setInputValue] = useState("");
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = async () => {
    const partsArray = inputValue.split(",").map(item => item.trim());

    try {
      // Send the 'days' data (inputValue) to the backend
      await axios.post("http://localhost:5000/api/forecast-data", { data: partsArray });
      setMessage("✅ Forecast data saved successfully!");
      setInputValue(""); // Clear input
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to save forecast data.");
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "auto", padding: "20px" }}>
      <h2>ADD Forecast Data</h2>
      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        placeholder="E"
        style={{ width: "100%", padding: "8px", marginBottom: "10px" }}
      />
      <button onClick={handleSubmit} style={{ padding: "10px 20px" }}>
        Submit
      </button>
      {message && <p style={{ marginTop: "15px" }}>{message}</p>}
    </div>
  );
}

export default GetForecasting;
