import React, { useState } from "react";

function PacketDetection() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [resultUrl, setResultUrl] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreview(URL.createObjectURL(file));
    setResultUrl(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const res = await fetch("http://localhost:5000/packet-upload", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    if (data.result_url) {
      setResultUrl(data.result_url);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Packet Detection</h2>
      <input type="file" onChange={handleImageChange} />
      <br /><br />
      {preview && (
        <div>
          <img src={preview} alt="Preview" height={300} />
          <br /><br />
          <button onClick={handleUpload}>Detect Packets</button>
        </div>
      )}
      <br />
      {resultUrl && (
        <div>
          <h4>Result:</h4>
          <img src={resultUrl} alt="Result" height={300} />
        </div>
      )}
    </div>
  );
}

export default PacketDetection;
