import React, { useState, useEffect } from "react";
import axios from "axios";

const InventoryTable = () => {
  const [inventory, setInventory] = useState([]);
  const [ingredient, setIngredient] = useState("");
  const [currentStock, setCurrentStock] = useState("");
  const [unit, setUnit] = useState("");
  const [lowStockThreshold, setLowStockThreshold] = useState("");
  const [reorderLevel, setReorderLevel] = useState("");
  const [expirationDate, setExpirationDate] = useState("");
  const [status, setStatus] = useState("");

  useEffect(() => {
    // Fetch inventory data
    axios
      .get("http://localhost:5000/inventory") // Replace with your backend URL
      .then((response) => {
        setInventory(response.data);
      })
      .catch((error) => {
        console.error("Error fetching inventory data:", error);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newInventoryItem = {
      Ingredient: ingredient,
      Current_Stock: parseFloat(currentStock),
      Unit: unit,
      Low_Stock_Threshold: parseInt(lowStockThreshold),
      Reorder_Level: parseInt(reorderLevel),
      Expiration_Date: new Date(expirationDate),
      Status: status,
    };

    // Create new inventory entry
    axios
      .post("http://localhost:5000/inventory", newInventoryItem)
      .then((response) => {
        setInventory([...inventory, response.data]);
        alert("Inventory added successfully!");
        resetForm();
      })
      .catch((error) => {
        console.error("Error adding inventory:", error);
      });
  };

  const resetForm = () => {
    setIngredient("");
    setCurrentStock("");
    setUnit("");
    setLowStockThreshold("");
    setReorderLevel("");
    setExpirationDate("");
    setStatus("");
  };

  return (
    <div>
      <h2>Inventory Management</h2>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Ingredient: </label>
          <input
            type="text"
            value={ingredient}
            onChange={(e) => setIngredient(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Current Stock: </label>
          <input
            type="number"
            value={currentStock}
            onChange={(e) => setCurrentStock(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Unit: </label>
          <input
            type="text"
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Low Stock Threshold: </label>
          <input
            type="number"
            value={lowStockThreshold}
            onChange={(e) => setLowStockThreshold(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Reorder Level: </label>
          <input
            type="number"
            value={reorderLevel}
            onChange={(e) => setReorderLevel(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Expiration Date: </label>
          <input
            type="date"
            value={expirationDate}
            onChange={(e) => setExpirationDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Status: </label>
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          >
            <option value="Available">Available</option>
            <option value="Low Stock">Low Stock</option>
            <option value="Expired">Expired</option>
          </select>
        </div>
        <button type="submit">Add Inventory</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Ingredient</th>
            <th>Current Stock</th>
            <th>Unit</th>
            <th>Low Stock Threshold</th>
            <th>Reorder Level</th>
            <th>Expiration Date</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((item) => (
            <tr key={item._id}>
              <td>{item.Ingredient}</td>
              <td>{item.Current_Stock}</td>
              <td>{item.Unit}</td>
              <td>{item.Low_Stock_Threshold}</td>
              <td>{item.Reorder_Level}</td>
              <td>{new Date(item.Expiration_Date).toLocaleDateString()}</td>
              <td>{item.Status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InventoryTable;
