import React, { useState, useEffect } from "react";
import axios from "axios";

function ManageCollections() {
  const [collectionName, setCollectionName] = useState("mcdonalds_customer_purchases");
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({});
  const [editingId, setEditingId] = useState(null);

  const fetchData = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/data/${collectionName}`);
      setData(res.data);
    } catch (err) {
      console.error("Fetch Error:", err);
    }
  };

  useEffect(() => {
    fetchData();
    setFormData({});
    setEditingId(null);
  }, [collectionName]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/data/${collectionName}/${id}`);
      fetchData();
    } catch (err) {
      console.error("Delete Error:", err);
    }
  };

  const handleEdit = (item) => {
    setFormData(item);
    setEditingId(item._id);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/data/${collectionName}/${editingId}`, formData);
      } else {
        await axios.post(`http://localhost:5000/data/${collectionName}`, formData);
      }
      fetchData();
      setFormData({});
      setEditingId(null);
    } catch (err) {
      console.error("Submit Error:", err);
    }
  };

  const renderTable = () => {
    if (data.length === 0) return <p>No data found.</p>;

    const headers = Object.keys(data[0]);

    return (
      <table border="1" cellPadding="5" style={{ width: "100%", marginTop: "20px" }}>
        <thead>
          <tr>
            {headers.map((key, i) => (
              <th key={i}>{key}</th>
            ))}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              {headers.map((key, i) => (
                <td key={i}>{item[key]?.toString()}</td>
              ))}
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item._id)} style={{ marginLeft: "10px" }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  const renderForm = () => {
    const keys = Object.keys(formData || {});
    return (
      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        {keys.map((key) => (
          key !== "_id" && (
            <div key={key}>
              <label>{key}: </label>
              <input
                type="text"
                name={key}
                value={formData[key] || ""}
                onChange={handleInputChange}
              />
            </div>
          )
        ))}
        <button type="submit" style={{ marginTop: "10px" }}>
          {editingId ? "Update" : "Add"}
        </button>
      </form>
    );
  };

  return (
    <div>
      <h2>Manage MongoDB Collections</h2>

      <select onChange={(e) => setCollectionName(e.target.value)} value={collectionName}>
        <option value="mcdonalds_customer_purchases">Customer Purchases</option>
        <option value="mcdonalds_ingredient_dataset">Ingredient Dataset</option>
        <option value="mcdonalds_ingredient_usage">Ingredient Usage</option>
        <option value="mcdonalds_sales_dataset">Sales Dataset</option>
        <option value="mcdonalds_shelf_life">Shelf Life</option>
      </select>

      {renderTable()}
      {renderForm()}
    </div>
  );
}

export default ManageCollections;
