import React, { useState } from "react";
import axios from "axios";

function GetForecasting() {
  const [inputValue, setInputValue] = useState("");
  const [message, setMessage] = useState("");
  const [forecastedData, setForecastedData] = useState(null);

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = async () => {
    const partsArray = inputValue.split(",").map(item => item.trim());

    try {
      // Send the data to the backend for processing
      const response = await axios.post("http://localhost:5000/api/forecast-data", { data: partsArray });

      // Get the forecasted data from the response
      setForecastedData(response.data.forecasted_data);
      setMessage("✅ Forecast data retrieved successfully!");
      setInputValue(""); // Clear input
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to retrieve forecast data.");
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "auto", padding: "20px" }}>
      <h2>GET Forecast Data</h2>
      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        placeholder="Enter data for forecasting"
        style={{ width: "100%", padding: "8px", marginBottom: "10px" }}
      />
      <button onClick={handleSubmit} style={{ padding: "10px 20px" }}>
        Submit
      </button>

      {message && <p style={{ marginTop: "15px" }}>{message}</p>}

      {/* {forecastedData && (
        <div className="output-section">
          <h3>Forecasted Results</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Forecasted Sales</th>
                <th>Inventory</th>
              </tr>
            </thead>
            <tbody>
              {forecastedData.map((item, index) => (
                <tr key={index}>
                  <td>{item['Ingredient']}</td>
                  <td>{item['Total_Quantity_Required']}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )} */}


{forecastedData && (
  <div className="output-section">
    <h3>Forecasted Results</h3>

    {/* First Table */}
    <h4>Ingredient Forecast</h4>
    <table className="table table-bordered">
      <thead>
        <tr>
          <th>Ingredient</th>
          <th>Forecasted Sales</th>
        </tr>
      </thead>
      <tbody>
        {forecastedData.ingredientData?.map((item, index) => (
          <tr key={index}>
            <td>{item['Ingredient']}</td>
            <td>{item['Sales']}</td>
          </tr>
        ))}
      </tbody>
    </table>

    {/* Second Table */}
    <h4>Inventory Forecast</h4>
    <table className="table table-bordered">
      <thead>
        <tr>
          <th>Inventory</th>
          <th>Total Quantity Required</th>
        </tr>
      </thead>
      <tbody>
        {forecastedData.inventoryData?.map((item, index) => (
          <tr key={index}>
            <td>{item['Inventory']}</td>
            <td>{item['Total_Quantity_Required']}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
)}

    </div>
  );
}

export default GetForecasting;
