import React, { useState} from 'react';
import {marked} from 'marked';
import axios from 'axios';
import './ChefChat.css';

const ChefChat = () => {
  const [input, setInput] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [sessionId, setSessionId] = useState(null);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'user', message: input };
    setChatHistory(prev => [...prev, userMessage]);

    try {
      const res = await axios.post('http://localhost:5000/chat', {
        message: input,
        session_id: sessionId
      });

      const botMessage = { sender: 'chef', message: res.data.response };
      setChatHistory(prev => [...prev, botMessage]);
      setSessionId(res.data.session_id);
      setInput('');
    } catch (err) {
      console.error('Error communicating with chatbot:', err);
    }
  };

  const handleKeyDown = e => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div className="chat-container">
      <h2>üë®‚Äçüç≥ Chef Bot</h2>
      <div className="chat-box">
        {chatHistory.map((msg, idx) => (
          <div key={idx} className={`chat-message ${msg.sender}`}>
            <strong>{msg.sender === 'user' ? 'You' : 'Chef'}:</strong>
            <div dangerouslySetInnerHTML={{ __html: marked.parase(msg.message) }}/>
          </div>
        ))}
      </div>
      <div className="input-box">
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Ask a cooking question..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChefChat;
