import React, { useState } from "react";

function PacketDetection() {
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [resultUrls, setResultUrls] = useState([]);

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImages(files);

    const previewUrls = files.map((file) => URL.createObjectURL(file));
    setPreviews(previewUrls);
    setResultUrls([]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    images.forEach((image, idx) => {
      formData.append("images", image); // key name should match backend
    });

    const res = await fetch("http://localhost:5000/packet-upload-multiple", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    if (data.result_urls) {
      setResultUrls(data.result_urls); // array of result image URLs
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Packet Detection</h2>
      <input type="file" multiple onChange={handleImageChange} />
      <br /><br />

      {previews.length > 0 && (
        <div>
          {previews.map((src, i) => (
            <img key={i} src={src} alt={`Preview ${i}`} height={200} style={{ margin: 10 }} />
          ))}
          <br />
          <button onClick={handleUpload}>Detect Packets</button>
        </div>
      )}

      <br />
      {resultUrls.length > 0 && (
        <div>
          <h4>Result:</h4>
          {resultUrls.map((url, i) => (
            <img key={i} src={url} alt={`Result ${i}`} height={200} style={{ margin: 10 }} />
          ))}
        </div>
      )}
    </div>
  );
}

export default PacketDetection;
