import React, { useState } from "react";
import axios from "axios";

const GetForecasting = () => {
  const [document1, setDocument1] = useState("");
  const [document2, setDocument2] = useState("");
  const [response, setResponse] = useState("");

  const handleForecast = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/forecast", {
        document1,
        document2,
      });
      setResponse(res.data.message);
    } catch (error) {
      setResponse("Error: " + error.message);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Get Forecasting</h1>

      <label className="block mb-2 font-semibold">Purchase Data (DOCUMENT1)</label>
      <textarea
        className="w-full p-2 border border-gray-300 rounded mb-4"
        rows={8}
        value={document1}
        onChange={(e) => setDocument1(e.target.value)}
      />

      <label className="block mb-2 font-semibold">Shelf Life Data (DOCUMENT2)</label>
      <textarea
        className="w-full p-2 border border-gray-300 rounded mb-4"
        rows={6}
        value={document2}
        onChange={(e) => setDocument2(e.target.value)}
      />

      <button
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        onClick={handleForecast}
      >
        Run Forecast
      </button>

      {response && (
        <div className="mt-6 p-4 bg-gray-100 border rounded text-sm whitespace-pre-wrap">
          {response}
        </div>
      )}
    </div>
  );
};

export default GetForecasting;
